sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var employeeGA = new GlideAjax('employeeUtils');
    employeeGA.addParam('sysparm_name', 'employeeEmailAndPhone');
    employeeGA.addParam('sysparm_collectEmployeeId', newValue);
    employeeGA.getXML(phoneAndEmailParse);

    function phoneAndEmailParse(response) {
        try {
            var answerFromXML = response.responseXML.documentElement.getAttribute("answer");
            answerFromXML = answerFromXML.evalJSON();

            g_form.setValue('manager_email', answerFromXML.varEmail);
            g_form.setValue('manager_mobile_number', answerFromXML.varPhone);
        } catch (err) {
            g_form.addErrorMessage(err);
        }
    }
}
