sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var REQstatus = g_form.getValue('state');
    switch (REQstatus) {
        case '1':
          g_form.addInfoMessage("E-Wallet Request is in Submitted State");
            break;
        case '2':
            g_form.addInfoMessage("E-Wallet Request is in Approval Requested State");
            break;
        case '3':
           g_form.addInfoMessage("E-Wallet Request is in Approved State State ♥");
            break;
        case '4':
           g_form.addInfoMessage("E-Wallet Request is in Completed  State ");
            break;
        case '5':
            g_form.addInfoMessage("E-Wallet Request is in Rejected State");
            break;
        case '6':
           g_form.addInfoMessage("E-Wallet Request is in Cancelled State");
            break;


    }
}
