sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
	g_form.getReference('manager',checkManager);
	function checkManager(obj){
		alert(obj.email);
		alert(obj.phone);
	}

    var Emailindexed = new GlideAjax('W5_Manager_Email');
    Emailindexed.addParam('sysparm_name', 'getEmailManager');
    Emailindexed.addParam('sysparm_collectManagerId', g_form.getValue('manager'));
    Emailindexed.getXML(EmailOfManagerParse);

    function EmailOfManagerParse(response) {
        try {
            var answerFromXML = response.responseXML.documentElement.getAttribute("answer");
            g_form.setValue('manager_email', answerFromXML);
        } catch (err) {
            g_form.addErrorMessage(err);
        }


        }
    }
