sys_script_include.script:
var employeeUtils = Class.create();
employeeUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    employeeEmailAndPhone: function() {
        var obj = {};
        obj.varEmail = '';
        obj.varPhone = '';
        var EmployeeId = this.getParameter('sysparm_collectEmployeeId');
        var EmployeeGR = new GlideRecord('x_1083671_corpgift_employee');
        if (EmployeeGR.get(EmployeeId)) {
            obj.varEmail = EmployeeGR.email;
            obj.varPhone = EmployeeGR.mobile_phone;
        }
        var json = new JSON();
        var data = json.encode(obj);
        return data;

    },

    type: 'employeeUtils'
});
