sys_script_include.script:
var W5_Manager_Email = Class.create();
W5_Manager_Email.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getEmailManager: function() {
        var returnValue = '';
        var IDManager = this.getParameter('sysparm_collectManagerId');
        var table = new GlideRecord('x_1083671_corpgift_employee');
        if (table.get(IDManager)) {
            returnValue = table.email;
        }
   return returnValue; },

    type: 'W5_Manager_Email'
});
